SELECT TYPE, CONTENTID ,ADD1, AREACODE, NVL(FIRSTIMAGE,'/images/icons/spot_sample.png') as FIRSTIMAGE, MAPX, MAPY, MLEVEL, TEL, TITLE
FROM PLACE WHERE AREACODE=#{areaCode} AND TYPE = #{placeType}  
AND ROWNUM <= 10

